import React, { useState } from 'react'
import { Canvas } from "@react-three/fiber";
import { Stars } from "@react-three/drei";
import { Physics } from "@react-three/cannon";
import PlaneOne from "../Box/PlaneOne";
import "../App.css";
import { Player } from "../Box/Player";
import { useBox } from "@react-three/cannon";
import { Html } from "@react-three/drei";
import "./Instruction.css";
import "./Texts.css";
const errorObj = require("../Data/data");
//errorObj["emailExist"]
const index = () => {
  return (
    <>
      <Canvas>
        <Stars />
        <ambientLight intensity={0.5} />
        <Physics>
          <About />
          <PlaneOne />
          <Player position={[0, 2, -30]} />
        </Physics>
      </Canvas>
    </>
  )
}
export default index;


const About = () => {
  const [pos, setPos] = useState("");
  const [ref] = useBox(() => ({
    position: pos,
  }));
  Object.keys(errorObj).map(ele => {
    setPos(ele.position)
    return (
      <>
        <mesh ref={ref}>
          <Html scale={1} rotation={[Math.PI / 1, 1.5, 3.1]} transform occlude>
            <div dangerouslySetInnerHTML={{ __html: ele.description }} />
          </Html>

          <meshLambertMaterial
            attach="material"
            color="black"
            transparent
            opacity={0.1}
            roughness={1}
            metalness={0}
          />
        </mesh>
      </>
    )
  })
};
