// sets the vertices of triangle `tri` with the 3 vertices after i
export function setTriangle(tri, i, index, pos) {
  var ta = tri.a;
  var tb = tri.b;
  var tc = tri.c;
  var i0 = i;
  var i1 = i + 1;
  var i2 = i + 2;
  if (index) {
    i0 = index.getX(i);
    i1 = index.getX(i + 1);
    i2 = index.getX(i + 2);
  }
  ta.x = pos.getX(i0);
  ta.y = pos.getY(i0);
  ta.z = pos.getZ(i0);
  tb.x = pos.getX(i1);
  tb.y = pos.getY(i1);
  tb.z = pos.getZ(i1);
  tc.x = pos.getX(i2);
  tc.y = pos.getY(i2);
  tc.z = pos.getZ(i2);
}
